import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom'
import axios from 'axios';
const Blog = () => {
    const blogId = useParams().id;
    const [blogData,setBlogData] = useState({});
    useEffect(()=>{
        const fetchBlogData = async()=>{
            try{
                const result = await axios.get(`http://localhost:3004/api/blog/${blogId}`)
                if(result.status === 200){
                    console.log(result.data)
                    setBlogData(result.data)
                }
            }catch(err){
                if(err.response){
                    alert(`Server Error : ${err.response.data}`)
                }
                else{
                    alert(`Network Error : ${err.message}`)
                }
            }
        }
        fetchBlogData();
    },[blogId])
  return (
    <div>
    <div className='px-52 flex flex-col items-center'>
        <h2 className='font-bold text-4xl mb-6'>{blogData.title}</h2>
        <p className="text-sm" style={{color:"#D1D5DB"}}>{blogData.createdAt}</p>
        <p className='text-lg font-bold mt-3'>by @{blogData.author_name}</p>
        <button className='py-2 w-1/4 rounded-lg text-white my-4' style={{backgroundColor:"#374151",cursor:"pointer"}}>Edit this post</button>
        <img src={`http://localhost:3004/${blogData.imgPath}`} alt='Blog related' className='object-cover w-full h-80'/>
        <p dangerouslySetInnerHTML={{__html:blogData.content}}></p>
    </div>
    </div>
  )
}

export default Blog
