import React, { useState } from 'react'
import { useNavigate } from 'react-router-dom';

const Login = ({setIsLoggedIn}) => {
  const navigate = useNavigate();
  const [email,setEmail] = useState('');
  const [password,setPassword] = useState('');
  const [loading,setLoading] = useState(false)
  const handleSubmit = async(e)=>{
    e.preventDefault();
    setLoading(true);
    const data = {
      email : email,
      password : password
    }
    const result = await fetch("http://localhost:3004/api/user/login",{
      method:"POST",
      headers:{
        "Content-Type":"application/json"
      },
      body:JSON.stringify(data)
    })
    if(result.ok){
      navigate("/")
      setIsLoggedIn(true)
    }
    setLoading(false)
  }
  return (
    <div className='px-20'>
      {!loading ? <>
       <h2 className='font-bold text-4xl text-center mb-5'>Login</h2>
        <form onSubmit={(e)=>handleSubmit(e)} className='flex flex-col items-center'>
        <input
         className='mb-3 py-2 w-1/3 px-3 rounded-sm'
         style={{border:"1px solid #374151"}}
         name="email" 
         type="email"
         placeholder='Email'
         value={email}
         onChange={(e)=>setEmail(e.target.value)}/>

        <input
         className='mb-3 py-2 w-1/3 px-3 rounded-sm'
         style={{border:"1px solid #374151"}}
         name="password" 
         type="password"
         placeholder='Password'
         value={password}
         onChange={(e)=>setPassword(e.target.value)}/>
        <button type='submit' className='py-2 w-1/3 rounded-sm text-white' style={{backgroundColor:"#374151"}}>Log in</button>
        </form></>
        :<>
        Loading......
        </>}
    </div>
  )
}

export default Login
