import React from 'react'
import logo from "../WhatsApp Image 2023-02-06 at 6.20.22 PM.jpeg"
const Card = ({blog}) => {
  const formatDate = (initialDate)=>{
    const date = new Date(initialDate);
    const options = {month:'long',year:'numeric',day:'numeric'}
    return date.toLocaleDateString(undefined,options);
  }
  return (
     <div className='flex mb-7' style={{cursor:"pointer"}} onClick={navigate(`/api/blog/:${blog._id}`)}>
     <img src={`http://localhost:3004/${blog.imgPath}`} alt='Blog Related' className='w-1/3 h-96 object-cover'/>
     <div className='flex flex-col w-3/5 ml-8 p-3'>
     <h2 className='font-bold text-4xl mb-6'>{blog.title}</h2>
     <div className='flex mb-7'>
         <p className='text-lg font-bold mr-2'>{blog.author_name}</p>
         <p className='text-lg'>{formatDate(blog.createdAt)}</p>
     </div>
     <p className='text-2xl'>{blog.summary}</p>
     </div>

 </div>
  )
}

export default Card
