  },[isLoggedIn,navigate])
  return (
    <div className='px-20'>
      {!loading ? <>
       <h2 className='font-bold text-4xl text-center mb-5'>Login</h2>
        <form onSubmit={(e)=>handleSubmit(e)} className='flex flex-col items-center'>
        <input
         className='mb-3 py-2 w-1/3 px-3 rounded-sm'
         style={{border:"1px solid #374151"}}
         name="email" 
         type="email"
         placeholder='Email'
         value={email}
         onChange={(e)=>setEmail(e.target.value)}/>

        <input
         className='mb-3 py-2 w-1/3 px-3 rounded-sm'
         style={{border:"1px solid #374151"}}
         name="password" 
         type="password"
         placeholder='Password'
         value={password}
         onChange={(e)=>setPassword(e.target.value)}/>
        <button type='submit' className='py-2 w-1/3 rounded-sm text-white' style={{backgroundColor:"#374151",cursor:"pointer"}}>Log in</button>
        </form></>
        :<>
        Loading......
        </>}
    </div>
  )
}

export default Login
