import React from 'react'
import axios from "axios"
import { useNavigate } from 'react-router-dom'
const Navbar = ({isLoggedIn,setIsLoggedIn}) => {
    const navigate = useNavigate()
    const handleNavigate = (page) => {
        navigate(page)
    }
    const handleLogOut = async()=>{
        const result = await axios.post("http://localhost:3004/api/user/logout");
        if(result.status === 200){
            setIsLoggedIn(false)
            handleNavigate("/login")
        }
    }
    return (
        <div className='flex py-16 px-20 justify-between'>
            <h2 className='font-bold text-2xl' onClick={()=>handleNavigate("/")}>MyBlog</h2>
            <ul className='flex w-1/12 text-xl justify-between'>
            {!isLoggedIn? <>
            <li onClick={() => handleNavigate("/login")} style={{cursor:"pointer"}}>Login</li>
                <li onClick={() => handleNavigate("/signup")} style={{cursor:"pointer"}}>Register</li>
            </>:
                <>
                <li onClick={() => handleNavigate("/newpost")}>Create New Post</li>
                <li onClick={handleLogOut}>Logout</li></>}

            </ul>
        </div>
    )
}

export default Navbar
