import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom'
import axios from 'axios';
const Blog = () => {
    const blogId = useParams().id;
    const [blogData,setBlogData] = useState({});
    useEffect(()=>{
        const fetchBlogData = async()=>{
            try{
                const result = await axios.get(`http://localhost:3004/api/blog/${blogId}`)
                if(result.status === 200){
                    console.log(result.data)
                    setBlogData(result.data)
                }
            }catch(err){
                if(err.response){
                    alert(`Server Error : ${err.response.data}`)
                }
                else{
                    alert(`Network Error : ${err.message}`)
                }
            }
        }
        fetchBlogData();
    },[blogId])
  return (
    <div className='px-52 flex flex-col'>
        <h2 className='font-bold text-4xl mb-6 text-center'>{blogData.title}</h2>
        <p className='text-center' style={{color:"#D1D5DB"}}>{blogData.createdAt}</p>
        <p className='text-lg font-bold text-center'>by @{blogData.author_name}</p>
        <button className='py-2 w-1/3 rounded-sm text-white' style={{backgroundColor:"#374151",cursor:"pointer"}}>Edit this post</button>
        <img src={`http:localhost:3004/${blogData.imgPath}`} alt='Blog related'/>
        <p>{blogData.content}</p>
    </div>
  )
}

export default Blog
