import React, { useState } from 'react'
import { useNavigate } from 'react-router-dom';

const Signup = () => {
  const navigate = useNavigate();
  const [email,setEmail] = useState('');
  const [password,setPassword] = useState('');
  const [username,setUsername] = useState('');

  const handleSubmit = async(e)=>{
    e.preventDefault();
    const data = {
      email : email,
      username:username,
      password : password
    }
    const result = await fetch("http://localhost:3004/signup",{
      method:"POST",
      headers:{
        "Content-Type":"application/json"
      },
      body:JSON.stringify(data)
    })
    if(result.ok){
      navigate("/login")
    }
  }
  return (
    <div className='px-20'>
      <h2 className='font-bold text-4xl text-center mb-5'>Register</h2>
    <form onSubmit={(e)=>handleSubmit(e)} className='flex flex-col items-center'>
        <input 
        className='mb-3 py-2 w-1/3 px-3 rounded-sm'
        style={{border:"1px solid #6B7280"}}
        name="email" 
        type="email" 
        value={email}
        onChange={(e)=>setEmail(e.target.value)}
        placeholder="Email"/>
        <input 
        className='mb-3 py-2 w-1/3 px-3 rounded-sm'
        style={{border:"1px solid #6B7280"}}
        name="username" 
        type="text" 
        value={username}
        onChange={(e)=>setUsername(e.target.value)}
        placeholder="Username"/>
        <input 
         className='mb-3 py-2 w-1/3 px-3 rounded-sm'
         style={{border:"1px solid #6B7280"}}
        name="password" 
        placeholder='Password'
        value={password}
        onChange={(e)=>setPassword(e.target.value)}
        type="password"/>
        <button type="submit" className='py-2 w-1/3 rounded-sm text-white' style={{backgroundColor:"#6B7280"}}>Sign up</button>
    </form>
    </div>
  )
}

export default Signup
