import React, { useContext, useEffect, useState } from 'react'
import { useNavigate } from 'react-router-dom';
import axios from "axios"
import { AuthContext } from '../context';

const Login = () => {
  const navigate = useNavigate();
  const [email,setEmail] = useState('');
  const [password,setPassword] = useState('');
  const [loading,setLoading] = useState(false)
  const {isLoggedIn,setIsLoggedIn} = useContext(AuthContext);
  const handleSubmit = async()=>{
    setLoading(true);
    try{
      const result = await axios.post("http://localhost:3004/api/user/login",{
        email : email,
        password : password
      },{withCredentials:true});
      if(result.status === 200){
        // console.log(result.headers['set-cookie'])
        setLoading(false)
        navigate("/")
        setIsLoggedIn(true)
        localStorage.setItem("isLoggedIn",true)
      }
    }catch(err){
      // server side error
      if(err.response)
        {
          console.log(err.response);
          alert(`Error: ${err.response.data}`);
        }
    else{
      // network error 
      alert(`Error: ${err.message}`);
    }
    }
    finally{
      setLoading(false)
    }
      }
  useEffect(()=>{
    if(isLoggedIn){
      navigate("/")
    }
  },[isLoggedIn,navigate])
  return (
    <div className='px-5 lg:px-16 xl:px-32 2xl:px-52'>
      {!loading ? <>
       <h2 className='font-bold text-4xl text-center mb-5'>Login</h2>
        <form onSubmit={handleSubmit} className='flex flex-col items-center'>
        <input
         className='mb-3 py-2 w-full sm:w-2/3 md:w-3/5 lg:w-1/2 px-3 rounded-sm'
         style={{border:"1px solid #374151"}}
         name="email" 
         type="email"
         required
         placeholder='Email'
         value={email}
         onChange={(e)=>setEmail(e.target.value)}/>

        <input
         className='mb-3 py-2 w-full sm:w-2/3 md:w-3/5 lg:w-1/2 px-3 rounded-sm'
         style={{border:"1px solid #374151"}}
         name="password" 
         type="password"
         required
         placeholder='Password'
         value={password}
         onChange={(e)=>setPassword(e.target.value)}/>
        <button type='submit' className='py-2 w-full sm:w-2/3 md:w-3/5 lg:w-1/2 rounded-sm text-white' style={{backgroundColor:"#374151",cursor:"pointer"}}>Log in</button>
        </form></>
        :<>
        Loading......
        </>}
    </div>
  )
}

export default Login
