     <h2 className='font-bold text-4xl mb-6'>{blog.title}</h2>
     <div className='flex w-1/6 justify-between mb-7'>
         <p className='text-lg font-bold'>{blog.author_name}</p>
         <p className='text-lg'>{formatDate(blog.createdAt)}</p>
     </div>
     <p className='text-2xl'>{blog.content}</p>
     </div>

 </div>
  )
}

export default Card
